INSERT INTO `ask_goods` VALUES (1,4,'ECS000000','KD876','+',7,8,'',1,0.110,1665.60,1388.00,0.00,0,0,1,'LG 3g 876 支持 双模 2008年04月 灰色 GSM,850,900,1800,1900','','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 很多朋友都已经抢先体验了3G网络的可视通话、高速上网等功能。LG KD876手机<span style=\"font-size: x-large;\"><span style=\"color: rgb(255, 0, 0);\"><strong>支持TD-SCDMA/GSM双模单待</strong></span></span>，便于测试初期GSM网络和TD网络之间的切换和共享。</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; LG KD876手机整体采用银色塑料材质，<strong><span style=\"font-size: x-large;\"><span style=\"color: rgb(255, 0, 0);\">特殊的旋转屏设计是本机的亮点</span></span></strong>，而机身背部的300万像素摄像头也是首发的六款TD-SCDMA手机中配置最高的。LG KD876手机屏幕下方设置有外键盘，该键盘由左/右软键、通话/挂机键、返回键、五维摇杆组成，摇杆灵敏度很高，定位准确。KD876的内键盘由标准12个电话键和三个功能键、一个内置摄像头组成。三个功能键分别为视频通话、MP3、和菜单键，所有按键的手感都比较一般，键程适中，当由于按键排列过于紧密，快速发短信时很容易误按，用户在使用时一定要多加注意。LG KD876手机机身周边的接口设计非常简洁，手机的厚度主要来自屏幕旋转轴的长度，如果舍弃旋屏设计的话，估计<span style=\"font-size: x-large;\"><strong><span style=\"color: rgb(255, 0, 0);\">厚度可以做到10mm以下</span></strong></span>。</p>','images/200905/thumb_img/1_thumb_G_1240902890710.jpg','images/200905/goods_img/1_G_1240902890755.jpg','images/200905/source_img/1_G_1240902890895.gif',1,'',1,1,0,13,1240902890,100,0,1,1,1,0,0,1269589545,9,'',-1,-1,NULL,NULL),(4,8,'ECS000004','诺基亚N85原装充电器','+',0,1,'',17,0.000,69.60,58.00,0.00,0,0,1,'','','','images/200905/thumb_img/4_thumb_G_1241422402467.jpg','images/200905/goods_img/4_G_1241422402722.jpg','images/200905/source_img/4_G_1241422402919.jpg',1,'',1,0,0,0,1241422402,100,0,0,0,0,0,0,1242110662,6,'',-1,-1,NULL,NULL),(3,8,'ECS000002','诺基亚原装5800耳机','+',3,1,'',24,0.000,81.60,68.00,0.00,0,0,1,'','','','images/200905/thumb_img/3_thumb_G_1241422082679.jpg','images/200905/goods_img/3_G_1241422082168.jpg','images/200905/source_img/3_G_1241422082367.jpg',1,'',1,1,0,0,1241422082,100,0,0,0,0,0,0,1241972255,6,'',-1,-1,NULL,NULL),(5,11,'ECS000005','索爱原装M2卡读卡器','+',3,7,'',8,0.000,24.00,20.00,0.00,0,0,1,'','','','images/200905/thumb_img/5_thumb_G_1241422518886.jpg','images/200905/goods_img/5_G_124142251877